// নতুন সংযোজন: একই আয়াত একাধিকবার থাকলে তার তালিকা
const verseLocationMap = new Map();
surahData.forEach(surah => {
    surah.verses.forEach(verse => {
        const arabicText = verse.arabic.trim();
        if (!verseLocationMap.has(arabicText)) {
            verseLocationMap.set(arabicText, []);
        }
        verseLocationMap.get(arabicText).push({ surah_id: surah.id, verse_id: verse.id });
    });
});
